//dummy User
const dummyUser = {
  nickname: "윤민수",
  Post: [],
  Followings: [],
  Followers: []
}

//intial State
export const initialState = {
  isLoggedIn : false,
  user: null,
  signupData: {},
  loginData: {}
}

//Action 이름정하기
const LOG_IN = 'LOG_IN';
const LOG_OUT = 'LOG_OUT';
const SIGN_UP = 'SIGN_UP';
const LOG_IN_SUCCESS = 'LOG_IN_SUCCESS';
const LOG_IN_FAILURE = 'LOG_IN_FAILURE';
//Action 의 결과 적기
//여기서 type이 정말 중요하다.
//data 는 state값에 들어갈 데이터들을 정의해주는 곳이다.
export const loginAction = (data) => {
  return {
  type: LOG_IN,
  data: data
  }
}

export const logoutAction = {
  type : LOG_OUT,
}

export const signupAction = (data) => {
  return {
    type: SIGN_UP,
    data: data,
  }
}

//reducer 생성
//반드시 reducer는 action의 값을 받아 initialState를 바꿔주는 역할을 하기에 인자로써, initialState와 action을 넣어준다
const reducer = ( state = initialState, action ) => {
  switch(action.type) {
    case LOG_IN : {
      return {
        ...state,
      isLoggedIn: true,
      user: dummyUser,
      loginData: action.data
    }
  } 
    case LOG_OUT : {
      return {
        ...state,
        isLoggedIn: false,
        user : null
      }
    }

    case SIGN_UP : {
      return {
        ...state,
        signupData: action.data
        }
    }

    case LOG_IN_SUCCESS : {
      return {
        ...state,
        }
    }

    case LOG_IN_FAILURE : {
      return {
        ...state
        }
    }

    default: {
      return {
        ...state,
      }
    }  
  }
}

export default reducer;
