import React from 'react';
import { Form, Input, Button, Card, Avatar, Icon } from 'antd'

const dummy = {
  isLoggedIn : true,
  imagePaths : [],
  mainPosts : [{
    User: {
      id: 1,
      nickname: '윤민수',
    },
    content: '첫 번째 게시글',
    img: 'https://img1.daumcdn.net/thumb/R1280x0/?scode=mtistory2&fname=http%3A%2F%2Fcfile23.uf.tistory.com%2Fimage%2F2657B9505809B4B634FF66'
  }]
}

const Home = () => {
  return (
    <>
    {dummy.isLoggedIn && 
    <Form style={{margin: "10px 0 20px 0"}} encType='multipart/form-data'>
      <Input.TextArea masLength={140} placeholder="어떤 신기한 일이 있었나요?" />
      <div style={{marginTop:"10px"}}>
        <input type="file" multiple hidden />
        <Button>이미지 업로드</Button>
        <Button type="primary" style={{ float: 'right'}} htmlType="submit">짹짹</Button>
      </div>
      <div style={{marginTop:"40px"}}>
        {dummy.imagePaths.map( (v,i) => {
          return (
            <div key={v} style={{ display: 'inline-block'}}>
              <img src={'http://localhost:3000/' + v} style ={{width:'200px'}} alt={v} />
              <div>
                <Button>제거</Button>
              </div>
            </div>
          )
        })}
      </div>
    </Form>
    }
    {dummy.mainPosts.map( (c) => {
          return (
            <Card
            key={+c.createdAt}
            cover={c.img && <img alt="example" src={c.img} />}
            actions={[
              <Icon type="retweet" key="retweet" />,
              <Icon type="heart" key="heart" />,
              <Icon type="message" key="message" />,
              <Icon type="ellipsis" key="ellipsis" />
            ]}
            extra={<Button>팔로우</Button>}
            >
              <Card.Meta 
              avatar={<Avatar>{c.User.nickname[0]}</Avatar>}
              title={c.User.nickname}
              description={c.content}
              />
            </Card>
          )
        })}

    </>
  );
};

export default Home;
